import React, {Component} from 'react';
import {GoTrashcan} from 'react-icons/go'
import firebase from "./Firebase"

class AttendeeList extends Component {

  constructor(props) {
    super(props);

  }

  deleteAttendee(e, meetingID, attendeeID) {
    e.preventDefault();
    const ref = firebase.database().ref(`/meetings/${this.props.userID}/${meetingID}/attendees/${attendeeID}`);
    ref.remove();
  }

  render() {
    const admin = this.props.adminUser === this.props.userID ? true : false;
    return (
        <div className="row justify-content-center">
          {this.props.attendees && this.props.attendees.length ? (this.props.attendees.map((attendee) => {
            return (
                <div key={attendee.attendeeID} className="col-8 col-sm-6 col-md-4 col-lg-3 mb-2 p-0 px-1">
                  <div className="card ">

                    <div
                        className={'card-body px-3 py-2 d-flex align-items-center' + admin ? '' : 'justify-content-center'}>
                      {admin && (
                          <div className="btn-group pr-2">
                            <button className="btn btn-sm btn-outline-secondary" onClick={(e)=>{this.deleteAttendee(e, this.props.meetingID, attendee.attendeeID)}} title={"Delete Attendee"}>
                              <GoTrashcan className='pr-1'/>
                            </button>
                          </div>
                      )}
                      <div>{attendee.attendeeName}</div>
                    </div>
                  </div>
                </div>
            );
          })) : null}
        </div>

    );
  }
}

export default AttendeeList;
