import React, {useState, useEffect} from 'react';
import ReactDOM from 'react-dom';
import './index.css';


function GitHubUser({login}) {
  const [data, setData] = useState({});
  useEffect(()=> {
    fetch(`https://api.github.com/users/${login}`).then(res => res.json()).then((data)=> {console.log(data);setData(data)}).catch(console.log)
  },[]);
  return (
      <div>
        <h1>{data.login}</h1>
        <img src={data.avatar_url} alt="" width="100"/>
      </div>
  );
}

function App() {
  return (
      <>
        <GitHubUser login={"moontahoe"}/>
      </>
  );
}

ReactDOM.render(
    <App/>,
    document.getElementById('root')
);
