import React, {Component} from 'react';
import {GoTrashcan} from 'react-icons/go'
import firebase from './Firebase'

class MeetingsList extends Component {

  constructor(props) {
    super(props);
  }

  deleteMeeting(e, meetingId){
    e.preventDefault()
    const ref = firebase.database().ref(`meetings/${this.props.userID}/${meetingId}`);
    ref.remove();
  }

  render() {
    const {meetings} = this.props;
    const myMeetings = meetings.map(meeting => {
      return (
          <div className="list-group-item d-flex" key={meeting.meetingID}>
            <section className="button-grp align-self-center">
              <button title="Delete meeting" onClick={(e)=>{this.deleteMeeting(e,meeting.meetingID)}} className="btn btn-sm btn-outline-secondary">
                <GoTrashcan className="mr-1"/>
              </button>
              <button title="Check in" onClick={(e)=>{navigate()}} className="btn btn-sm btn-outline-secondary">
                <FaLink className="mr-1"/>
              </button>
            </section>
            <section className="pl-3 text-left align-self-center">
              {meeting.meetingName}
            </section>
          </div>

      );
    });
    return (
        <div>
          {myMeetings}
        </div>
    );
  }
}

export default MeetingsList;
